import { VERSION } from "@octokit/plugin-paginate-rest/dist-src/version";
import { paginate } from "@octokit/plugin-paginate-rest/dist-src/paginate";
import { iterator } from "@octokit/plugin-paginate-rest/dist-src/iterator";
export { composePaginateRest } from "@octokit/plugin-paginate-rest/dist-src/compose-paginate";
export { isPaginatingEndpoint, paginatingEndpoints, } from "@octokit/plugin-paginate-rest/dist-src/paginating-endpoints";
/**
 * @param octokit Octokit instance
 * @param options Options passed to Octokit constructor
 */
export function paginateRest(octokit) {
    return {
        paginate: Object.assign(paginate.bind(null, octokit), {
            iterator: iterator.bind(null, octokit),
        }),
    };
}
paginateRest.VERSION = VERSION;
